import FullCalendar from "@fullcalendar/react";
import timeGridPlugin from "@fullcalendar/timegrid";
import { useState } from "react";
import { API } from "../../util/api/Api.js";
import "./home.css";
import moment from "moment";

let api = new API();

function Home(props) {
  const [cronDetails, setCronDetails] = useState([]);
  const [timeMap, setTimeMap] = useState(new Map());
  const [events, setEvents] = useState([]);

  const getEventKey = (time) => {
    return moment(time).local().format("YYYY-MM-DDTkk:mm:ss");
  };

  const getEndTime = (time) => {
    const dateTime = new Date(time);
    dateTime.setSeconds(dateTime.getSeconds() + 1);
    return moment(dateTime).local().format("YYYY-MM-DDTkk:mm:ss");
  };

  const clearEvents = () => {
    setTimeMap(timeMap.clear());
    setEvents([]);
  };

  const populateStartTimeMap = (element, startTimeMap) => {
    var {
      name,
      clientName,
      timeZone,
      cron,
      cronInfo,
      env,
      exchange,
      reportCycle,
      createdTs,
      triggerTimes,
    } = element;
    triggerTimes.forEach((element) => {
      if (startTimeMap.has(getEventKey(element))) {
        const newList = [
          {
            name: name,
            clientName: clientName,
            timeZone: timeZone,
            cron: cron,
            cronInfo: cronInfo,
            env: env,
            exchange: exchange,
            reportCycle: reportCycle,
            createdTs: createdTs,
          },
        ];
        const existingList = startTimeMap.get(getEventKey(element));
        startTimeMap.set(getEventKey(element), existingList.concat(newList));
      } else {
        const list = [
          {
            name: name,
            clientName: clientName,
            timeZone: timeZone,
            cron: cron,
            cronInfo: cronInfo,
            env: env,
            exchange: exchange,
            reportCycle: reportCycle,
            createdTs: createdTs,
          },
        ];
        startTimeMap.set(getEventKey(element), list);
      }
    });
    return startTimeMap;
  };

  const handleEventClick = ({ event }) => {
    const key = getEventKey(event.start);
    var messages = [];
    Object.values(timeMap.get(key)).forEach((element) => {
      const message =
        element.name +
        " " +
        element.clientName +
        " (" +
        element.env +
        ") " +
        element.exchange +
        " " +
        element.timeZone +
        " " +
        element.cron +
        " (" +
        element.cronInfo +
        ") ";
      console.log(message);
      messages.push(element.name + element.cron);
    });
    //console.log(message);
  };

  const setNewEvents = (cronDetails) => {
    var startTimeMap = new Map();
    var newEvents = [];
    cronDetails.forEach((element) => {
      populateStartTimeMap(element, startTimeMap);
    });
    startTimeMap.forEach((value, key) => {
      newEvents.push({
        title: "Event " + newEvents.length,
        start: key,
        end: getEndTime(key),
      });
    });
    setEvents(newEvents);
    setTimeMap(startTimeMap);
  };

  const handleReloadClick = () => {
    return new Promise((resolve) => {
      api
        .getTriggerTimes()
        .then((res) => {
          clearEvents();
          setCronDetails(res.data.message);
          setNewEvents(res.data.message);
        })
        .catch((err) => {
          console.log(err);
        });
    });
  };

  return (
    <div>
      <div>
        <FullCalendar
          plugins={[timeGridPlugin]}
          timeZone="local"
          initialView="timeGridDay"
          allDaySlot={false}
          eventMinHeight={20}
          customButtons={{
            reload: {
              text: "Reload",
              click: function () {
                handleReloadClick();
              },
            },
          }}
          headerToolbar={{
            left: "reload,prev,next",
            right: "today,timeGridDay,timeGridWeek",
          }}
          eventClick={handleEventClick}
          slotDuration="00:05:00"
          events={events}
        />
      </div>
    </div>
  );
}

export default Home;
