import FullCalendar from "@fullcalendar/react";
import timeGridPlugin from "@fullcalendar/timegrid";
import { useState, useEffect } from "react";
import { API } from "../../util/api/Api.js";
import "./home.css";
import moment from "moment";
import POPup from "../../popup/popup.jsx";

let api = new API();

function Home(props) {
  const [cronDetails, setCronDetails] = useState([]);
  const [timeMap, setTimeMap] = useState(new Map());
  const [events, setEvents] = useState([]);
  const [displayMap, setDisplayMap] = useState({});
  const [popup, setPopup] = useState(false);
  const stateValue = { popup, setPopup };
  const [timer, setTimer] = useState(false);
  const [firstRender, setFirstRender] = useState(true);
  const [movementEvents, setMovementEvents] = useState([]);
  const [movements, setMovements] = useState([]);
  const [baseEvents, setBaseEvents] = useState([]);

  const getEventKey = (time) => {
    return moment(time).local().format("YYYY-MM-DDTkk:mm:ss");
  };

  const getEndTime = (time) => {
    const dateTime = new Date(time);
    dateTime.setSeconds(dateTime.getSeconds() + 1);
    return moment(dateTime).local().format("YYYY-MM-DDTkk:mm:ss");
  };

  const clearEvents = () => {
    setTimeMap(timeMap.clear());
    setEvents([]);
  };

  useEffect(() => {
    if (firstRender) {
      handleReload();
      setFirstRender(false);
    }
    getMovementDetails();
    countTimer();
    //eslint-disable-next-line
  }, [timer]);

  function countTimer() {
    setTimeout(() => {
      setTimer(!timer);
    }, 10000);
  }

  const populateStartTimeMap = (element, startTimeMap) => {
    var {
      name,
      clientName,
      timeZone,
      cron,
      cronInfo,
      env,
      exchange,
      reportCycle,
      createdTs,
      triggerTimes,
    } = element;
    triggerTimes.forEach((element) => {
      if (startTimeMap.has(getEventKey(element))) {
        const newList = [
          {
            name: name,
            clientName: clientName,
            timeZone: timeZone,
            cron: cron,
            cronInfo: cronInfo,
            env: env,
            exchange: exchange,
            reportCycle: reportCycle,
            createdTs: createdTs,
          },
        ];
        const existingList = startTimeMap.get(getEventKey(element));
        startTimeMap.set(getEventKey(element), existingList.concat(newList));
      } else {
        const list = [
          {
            name: name,
            clientName: clientName,
            timeZone: timeZone,
            cron: cron,
            cronInfo: cronInfo,
            env: env,
            exchange: exchange,
            reportCycle: reportCycle,
            createdTs: createdTs,
          },
        ];
        startTimeMap.set(getEventKey(element), list);
      }
    });
    return startTimeMap;
  };

  const handleEventClick = ({ event }) => {
    const key = getEventKey(event.start);
    if (event.title.includes("Event")) {
      var messages = [];
      Object.values(timeMap.get(key)).forEach((element) => {
        const message =
          element.name +
          " " +
          element.clientName +
          " (" +
          element.env +
          ") " +
          element.exchange +
          " " +
          element.cron +
          " (" +
          element.cronInfo +
          " " +
          element.timeZone +
          ") ";
        messages.push(message);
      });
      setDisplayMap({ key: key, values: messages });
    } else if (event.title.includes("Movement")) {
      const element = getMovement(event.title);
      const messages = [element.id + " " + element.status];
      setDisplayMap({
        key: `${element.counterParty + " " + element.title}`,
        values: messages,
      });
    }
    setPopup(true);
  };

  function getMovement(title) {
    var obj;
    movements.forEach((element) => {
      if (element.title === title) {
        obj = element;
      }
    });
    return obj;
  }

  function populateMovementEvents(data) {
    var movementEvents = [];
    var alteredMovements = [];
    data.forEach((element) => {
      movementEvents.push({
        title: "Movement " + movementEvents.length,
        start: getEventKey(element.createdTs),
        end: getEndTime(getEventKey(element.createdTs)),
        color: "red",
      });
      alteredMovements.push({
        ...element,
        title: "Movement " + alteredMovements.length,
      });
    });
    if (baseEvents.length > 0) {
      setMovementEvents(movementEvents);
      setEvents(baseEvents.concat(movementEvents));
      setMovements(alteredMovements);
    }
  }

  const setNewEvents = (cronDetails) => {
    var startTimeMap = new Map();
    var newEvents = [];
    cronDetails.forEach((element) => {
      populateStartTimeMap(element, startTimeMap);
    });
    startTimeMap.forEach((value, key) => {
      if (!isNaN(new Date(key).getTime())) {
        newEvents.push({
          title: "Event " + newEvents.length,
          start: key,
          end: getEndTime(key),
        });
      }
    });
    setBaseEvents(newEvents);
    setEvents(newEvents);
    getMovementDetails()
    setTimeMap(startTimeMap);
  };

  const handleReload = () => {
    return new Promise((resolve) => {
      api
        .getTriggerTimes()
        .then((res) => {
          clearEvents();
          setCronDetails(res.data.message);
          setNewEvents(res.data.message);
        })
        .catch((err) => {
          console.log(err);
        });
    });
  };

  const getMovementDetails = () => {
    return new Promise((resolve) => {
      api
        .getMovementRecords()
        .then((res) => {
          populateMovementEvents(res.data.message);
        })
        .catch((err) => {
          console.log(err);
        });
    });
  };

  return (
    <div>
      <div>
        <POPup elementsMap={displayMap} stateValue={stateValue}></POPup>
        <FullCalendar
          plugins={[timeGridPlugin]}
          timeZone="local"
          initialView="timeGridWeek"
          allDaySlot={false}
          eventMinHeight={20}
          headerToolbar={{
            left: "prev,next",
            center: "title",
            right: "today",
          }}
          buttonText={{ today: "Current Week" }}
          eventClick={handleEventClick}
          slotDuration="00:10:00"
          events={events}
          slotLabelInterval="00:30:00"
          nowIndicator={true}
        />
      </div>
    </div>
  );
}

export default Home;
