import React from "react";
import { useState } from "react";
import FormInput from "../form-input/FormInput";
import Button from "../button/Button";
import config from "../../config";
import FormSelector from "../form-selector/FormSelector";
import { API } from "../util/api/Api.js";
import "./cronInputForm.css";
let api = new API();
const index = 0;

const defaultOptions = {
  clientNames: getClientName(config.clients),
  exchanges: getExchangeName(config.exchanges),
  envs: getEnvs(getClientName(config.clients)[index].value),
};

const defaultFormFields = {
  cron: "",
  name: "",
  clientName: defaultOptions.clientNames[index].value,
  exchange: defaultOptions.exchanges[index].value,
  env: defaultOptions.envs[index].value,
};

function getExchangeName(data) {
  return Object.values(data);
}

function getEnvs(client) {
  return Object.values(config.clients[client].envs);
}

function getClientName(data) {
  var clients = [];
  Object.values(data).forEach((element) => {
    clients.push(element.name);
  });
  return clients;
}

function CronInputForm(props) {
  const [formFields, setFormFields] = useState(defaultFormFields);
  const { name, cron, clientName, exchange, env } = formFields;
  const [cronMessage, setCronMessage] = useState("cron");

  const [options, setOptions] = useState(defaultOptions);
  const { clientNames, exchanges, envs } = options;
  const handleSubmit = () => {
    // console.log(getClientName(config.clients));
  };

  const getCronInfo = (cron) => {
    return new Promise((resolve) => {
      api
        .getCronInfo(cron)
        .then((res) => {
          setCronMessage(res.data.message);
        })
        .catch((err) => {
          console.log(err);
        });
    });
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormFields({ ...formFields, [name]: value });
    if (name === "clientName") {
      const newEnvs = getEnvs(value);
      setOptions({ ...options, envs: newEnvs });
      setFormFields({ ...formFields, env: newEnvs[index].value });
    } else if (name === "cron") {
      if (value.trim().length > 0) {
        getCronInfo(value);
      } else setCronMessage("cron");
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <FormInput
          label="Name"
          type="text"
          required
          onChange={handleChange}
          name="name"
          value={name}
        />

        <FormInput
          label={cronMessage}
          type="text"
          required
          onChange={handleChange}
          name="cron"
          value={cron}
        />
        <div className="style">
          <FormSelector
            label="Client Names"
            options={clientNames}
            onChange={handleChange}
            name="clientName"
          />
          <FormSelector
            label="Exchanges"
            options={exchanges}
            onChange={handleChange}
            name="exchange"
          />
          <FormSelector
            label="Envs"
            options={envs}
            name="env"
            onChange={handleChange}
          />
        </div>
        <div className="style">
          <Button type="button">Clear</Button>
          <Button buttonType="google" type="submit">
            Submit
          </Button>
        </div>
      </form>
    </div>
  );
}

export default CronInputForm;
