import React from "react";
import { useState } from "react";
import FormInput from "../form-input/FormInput";
import Button from "../button/Button";
import config from "../../config";
import FormSelector from "../form-selector/FormSelector";

const defaultFormFields = {
  cron: "",
  name: "",
  clientNames: getClientName(config.clients),
  exchanges: getExchangeName(config.exchanges),
};

function getExchangeName(data) {
  return Object.values(data);
}

function getClientName(data) {
  var clients = [];
  Object.values(data).forEach((element) => {
    clients.push(element.name);
  });
  return clients;
}

function CronInputForm(props) {
  const [formFields, setFormFields] = useState(defaultFormFields);
  const { name, cron, clientNames,exchanges } = formFields;

  const handleSubmit = () => {
    // console.log(getClientName(config.clients));
  };

  const handleChange = (event) => {
    const { name, value } = event.target;

    setFormFields({ ...formFields, [name]: value });
  };
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <FormInput
          label="Name"
          type="text"
          required
          onChange={handleChange}
          name="name"
          value={name}
        />

        <FormInput
          label="Cron"
          type="text"
          required
          onChange={handleChange}
          name="cron"
          value={cron}
        />
        <FormSelector label="Client Names" options={clientNames} />
        <FormSelector label="Exchanges" options={exchanges} />
        <Button type="submit">Submit</Button>
      </form>
    </div>
  );
}

export default CronInputForm;
