import FullCalendar from "@fullcalendar/react";
import timeGridPlugin from "@fullcalendar/timegrid";
import { useState } from "react";
import { API } from "../../util/api/Api.js";
import "./home.css";
import moment from "moment";

let api = new API();

function Home(props) {
  var cronDetails = [];
  var startTimeMap = new Map();
  const [events,setEvents]=useState([]);

  const getEventKey = (time) => {
    return moment(time).local().format("YYYY-MM-DDTkk:mm:ss");
  };

  const getEndTime = (time) => {
    const dateTime = new Date(time);
    dateTime.setSeconds(dateTime.getSeconds() + 1);
    return moment(dateTime).local().format("YYYY-MM-DDTkk:mm:ss");
  };

  const clearEvents =()=>{
    setEvents([]);
  }

  const populateStartTimeMap = (element) => {
    var {
      name,
      clientName,
      timeZone,
      cron,
      cronInfo,
      env,
      exchange,
      reportCycle,
      createdTs,
      triggerTimes,
    } = element;
    triggerTimes.forEach((element) => {
      if (startTimeMap.has(getEventKey(element))) {
        const newList = [
          {
            name: name,
            clientName: clientName,
            timeZone: timeZone,
            cron: cron,
            cronInfo: cronInfo,
            env: env,
            exchange: exchange,
            reportCycle: reportCycle,
            createdTs: createdTs,
          },
        ];
        const existingList = startTimeMap.get(getEventKey(element));
        startTimeMap.set(
          getEventKey(element),
          existingList.concat(newList)
        );
      } else {
        const list = [
          {
            name: name,
            clientName: clientName,
            timeZone: timeZone,
            cron: cron,
            cronInfo: cronInfo,
            env: env,
            exchange: exchange,
            reportCycle: reportCycle,
            createdTs: createdTs,
          },
        ];
        startTimeMap.set(getEventKey(element), list);
      }
    });
  };

  const setNewEvents=(cronDetails)=>{
    var newEvents = [];
    cronDetails.forEach((element) => {
      populateStartTimeMap(element, startTimeMap);
    });
    startTimeMap.forEach((value, key) => {
      newEvents.push({
        title: key,
        start: key,
        end: getEndTime(key),
      });
    });
    setEvents(newEvents);
  }

  const handleReloadClick = () => {
    return new Promise((resolve) => {
      api
        .getTriggerTimes()
        .then((res) => {
          clearEvents();
          cronDetails = res.data.message;
          setNewEvents(cronDetails);
        })
        .catch((err) => {
          console.log(err);
        });
    });
  };

  return (
    <div>
      <div>
        <FullCalendar
          plugins={[timeGridPlugin]}
          timeZone="local"
          initialView="timeGridDay"
          allDaySlot={false}
          eventMinHeight={20}
          displayEventTime={false}
          customButtons={{
            reload: {
              text: "Reload",
              click: function () {
                handleReloadClick();
              },
            },
          }}
          headerToolbar={{
            left: "reload,prev,next",
            right: "today,timeGridDay,timeGridWeek",
          }}
          eventClick={function (arg) {
            alert(arg.event.title);
            alert(getEventKey(arg.event.start));
          }}
          slotDuration="00:05:00"
          events={events}
        />
      </div>
    </div>
  );
}

export default Home;
