import React from "react";
import { useState, useEffect } from "react";
import FormInput from "../form-input/FormInput";
import Button from "../button/Button";
import config from "../../config";
import FormSelector from "../form-selector/FormSelector";
import { API } from "../util/api/Api.js";
import "./cronInputForm.css";
let api = new API();
const startIndex = 0;

const defaultOptions = {
  clientNames: getClientName(config.clients),
  exchanges: getExchangeName(config.exchanges),
  envs: getEnvs(getClientName(config.clients)[startIndex].value),
  timeZones: getTimeZones(config.timeZones),
  reportCycles: getReportCycles(config.reportCycles),
  ids: [],
};

const defaultFormFields = {
  cron: "",
  name: "",
  clientName: defaultOptions.clientNames[startIndex].value,
  exchange: defaultOptions.exchanges[startIndex].value,
  env: defaultOptions.envs[startIndex].value,
  timeZone: defaultOptions.timeZones[startIndex].value,
  reportCycle: defaultOptions.reportCycles[startIndex].value,
  id: 1,
};

function getTimeZones(data) {
  var timeZones = [];
  Object.values(data).forEach((element) => {
    timeZones.push(element.name);
  });
  return timeZones;
}

function getExchangeName(data) {
  return Object.values(data);
}

function getReportCycles(data) {
  return Object.values(data);
}

function getEnvs(client) {
  return Object.values(config.clients[client].envs);
}

function getClientName(data) {
  var clients = [];
  Object.values(data).forEach((element) => {
    clients.push(element.name);
  });
  return clients;
}

function CronInputForm(props) {
  const [formFields, setFormFields] = useState(defaultFormFields);
  const { name, cron, clientName, exchange, env, timeZone, reportCycle, id } =
    formFields;

  const [cronMessage, setCronMessage] = useState("cron");

  const [records, setRecords] = useState([]);

  const [addNewEntryStatus, setAddNewEntryStatus] = useState(false);

  const [options, setOptions] = useState(defaultOptions);
  const { clientNames, exchanges, envs, timeZones, reportCycles, ids } =
    options;

  useEffect(() => {
    getEntries();
    console.log("Cron Input form page reloaded");
    //eslint-disable-next-line
  }, [addNewEntryStatus]);

  const handleReset = () => {
    setFormFields(defaultFormFields);
    setOptions(defaultOptions);
    setCronMessage("cron");
    setAddNewEntryStatus(!addNewEntryStatus);
  };
  const addNewEntry = (event) => {
    event.preventDefault();
    return new Promise((resolve) => {
      api
        .addNewEntry(formFields)
        .then((res) => {
          alert(res.data.message);
          if (res.data.message.includes("Success")) {
            handleReset();
          }
        })
        .catch((err) => {
          console.log(err);
        });
    });
  };

  const getEntries = () => {
    return new Promise((resolve) => {
      api
        .getRecords()
        .then((res) => {
          setRecords(res.data.message);
          var ids = [];
          res.data.message.forEach((element) => {
            ids.push({ label: element.id, value: element.id });
          });
          setOptions({ ...options, ids: ids });

        })
        .catch((err) => {
          console.log(err);
        });
    });
  };

  const handleUpdate = () => {
    if (
      formFields.name.trim().length > 0 &&
      formFields.cron.trim().length > 0
    ) {
      return new Promise((resolve) => {
        api
          .updateEntry(formFields)
          .then((res) => {
            alert(res.data.message);
            if (res.data.message.includes("Success")) {
              handleReset();
            }
          })
          .catch((err) => {
            console.log(err);
          });
      });
    } else {
      alert("Please enter name and valid cron");
    }
  };

  const setCronInfo = (cron) => {
    return new Promise((resolve) => {
      api
        .getCronInfo(cron)
        .then((res) => {
          setCronMessage(res.data.message);
        })
        .catch((err) => {
          console.log(err);
        });
    });
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    var newFormField = formFields;
    newFormField = { ...newFormField, [name]: value };
    if (name === "clientName") {
      const newEnvs = getEnvs(value);
      setOptions({ ...options, envs: newEnvs });
      newFormField = { ...newFormField, env: newEnvs[startIndex].value };
    } else if (name === "cron") {
      if (value.trim().length > 0) {
        setCronInfo(value);
      } else setCronMessage("cron");
    } else if (name === "id") {
      const record = getRecord(value,records);
      newFormField = {
        ...newFormField,
        name: record.name,
        cron: record.cron,
        exchange: record.exchange,
        env:record.env,
        timeZone:record.timeZone,
        reportCycle:record.reportCycle,
        clientName:record.clientName,
      };
    }
    setFormFields(newFormField);
  };

  const getRecord = (id,entries) => {
    var obj;
    records.forEach((element) => {
      if (element.id.toString() === id.toString()) {
        obj = element;
      }
    });
    return obj;
  };

  return (
    <div>
      <form onSubmit={addNewEntry}>
        <FormInput
          label="Name"
          type="text"
          required
          onChange={handleChange}
          name="name"
          value={name}
        />

        <FormInput
          label={cronMessage}
          type="text"
          required
          onChange={handleChange}
          name="cron"
          value={cron}
        />
        <div className="style">
          <FormSelector
            label="ID"
            options={ids}
            name="id"
            onChange={handleChange}
            value={id}
          />
          <FormSelector
            label="Client Names"
            options={clientNames}
            onChange={handleChange}
            name="clientName"
            value={clientName}
          />
          <FormSelector
            label="Exchanges"
            options={exchanges}
            onChange={handleChange}
            name="exchange"
            value={exchange}
          />
          <FormSelector
            label="Envs"
            options={envs}
            name="env"
            onChange={handleChange}
            value={env}
          />
          <FormSelector
            label="Report Cycles"
            options={reportCycles}
            name="reportCycle"
            onChange={handleChange}
            value={reportCycle}
          />
          <FormSelector
            label="Time Zones"
            options={timeZones}
            name="timeZone"
            onChange={handleChange}
            value={timeZone}
          />
        </div>
        <div className="style">
          <Button buttonType="google" type="submit">
            ADD
          </Button>
          <Button type="button" onClick={handleReset}>
            Clear
          </Button>
        </div>
        <div className="style">
          <Button type="button" onClick={handleUpdate}>
            Update
          </Button>
        </div>
      </form>
      {records.length > 0 ? (
        <div className="App">
          <table>
            <tbody>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Client</th>
                <th>Env</th>
                <th>Exchange</th>
                <th>Cron</th>
                <th>Time Zone</th>
                <th>Report Cycle</th>
                <th>CreatedTs</th>
              </tr>
              {records.map((val, key) => {
                return (
                  <tr key={key}>
                    <td>{val.id}</td>
                    <td>{val.name}</td>
                    <td>{val.client}</td>
                    <td>{val.env}</td>
                    <td>{val.exchange}</td>
                    <td>{val.cron}</td>
                    <td>{val.time_zone}</td>
                    <td>{val.report_cycle}</td>
                    <td>{val.created_time}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      ) : (
        ""
      )}
    </div>
  );
}

export default CronInputForm;
