import React from "react";
import "./popup.css";

function POPUP({ elementsMap, stateValue, ...otherProps }) {
  const { popup, setPopup } = stateValue;
  const { title, records } = elementsMap;

  const handleEventClick = () => {
    setPopup(false);
  };

  function onlyUnique(value, index, self) {
    return self.indexOf(value) === index;
  }

  const getAllUniqueValues = (index) => {
    var values = [];
    values.push("ALL");
    values.push((
      typeof records !== "undefined" && records.length > 0
        ? records.map((value, key) => {
            return Object.values(value)[index];
          })
        : []
    ).filter(onlyUnique));
    return values;
  };

  return (
    <div className={popup ? "popup" : "hide"}>
      <div className="title">{title}</div>
      {typeof records !== "undefined" && records.length > 0 ? (
        <table className="table">
          <tbody>
            <tr>
              {Object.keys(records[0]).map((element, index) => {
                return (
                  <th className="th" key={element}>
                    <div>
                      {element}
                      <select className="filter">
                        {getAllUniqueValues(index).map((option) => (
                          <option key={option} value={option}>
                            {option}
                          </option>
                        ))}
                      </select>
                    </div>
                  </th>
                );
              })}
            </tr>
            {records.map((val, key) => {
              return (
                <tr key={key} className="tr">
                  {Object.values(val).map((value) => {
                    return (
                      <td className="td" key={Math.random()}>
                        {value}
                      </td>
                    );
                  })}
                </tr>
              );
            })}
          </tbody>
        </table>
      ) : (
        ""
      )}
      <div className="dismiss-btn">
        <button className="dismiss-popup-btn" onClick={handleEventClick}>
          Dismiss
        </button>
      </div>
    </div>
  );
}

export default POPUP;
