import React from "react";
import { useSelector } from "react-redux";
import { Fragment } from "react";
import { Outlet, Link } from "react-router-dom";
import { ReactComponent as BatonLogo } from "../../../logo/logo-login-new.svg";
import "./navigation.styles.scss";
import { signInWithGooglePopup } from "../../util/firebase/Firebase";

import { SelectCurrentUser } from "../../store/user/UserSelector";
import { signOutUser } from "../../util/firebase/Firebase";
function Navigation(props) {
  const currentUser = useSelector(SelectCurrentUser);

  const signInWithGoogle = async () => {
    await signInWithGooglePopup();
  };

  return (
    <Fragment>
      <div className="navigation">
        <Link className="logo-container" to="/">
          <BatonLogo className="logo" />
        </Link>
        <div className="nav-links-container">
          {currentUser ? (
            <Link className="nav-link" to="/home">
              HOME
            </Link>
          ) : (
            <Link className="nav-link" to="/">
            </Link>
          )}

          {currentUser ? (
            <Link className="nav-link" to="/editor">
              EDIT
            </Link>
          ) : (
            ""
          )}
          {currentUser ? (
            <span className="nav-link" onClick={signOutUser}>
              SIGN OUT
            </span>
          ) : (
            <span className="nav-link" onClick={signInWithGoogle}>
              SIGN IN
            </span>
          )}
        </div>
      </div>
      <Outlet />
    </Fragment>
  );
}

export default Navigation;
