import FullCalendar from "@fullcalendar/react";
import timeGridPlugin from "@fullcalendar/timegrid";
import { useState } from "react";
import { API } from "../../util/api/Api.js";
import "./home.css";
import moment from "moment";

let api = new API();

function Home(props) {
  const [cronDetails, setCronDetails] = useState([]);

  const clearEvents = () => {
    setCronDetails([]);
  };

  const getStartTime = (time) => {
    return moment(time).local().format("YYYY-MM-DDTkk:mm:ss");
  };

  const convertToString = (time)=>{
    return moment(time).format("YYYY-MM-DDTkk:mm:ss");
  }

  const getEndTime = (time) => {
    const dateTime = new Date(time);
    dateTime.setSeconds(dateTime.getSeconds() + 1);
    return moment(dateTime).local().format("YYYY-MM-DDTkk:mm:ss");
  };

  const handleReloadClick = () => {
    return new Promise((resolve) => {
      api
        .getTriggerTimes()
        .then((res) => {
          clearEvents();
          var startTimeMap = new Map();
          res.data.message.forEach((element) => {
            var {
              name,
              clientName,
              timeZone,
              cron,
              cronInfo,
              env,
              exchange,
              reportCycle,
              createdTs,
              triggerTimes,
            } = element;
            triggerTimes.forEach((element) => {
              if (startTimeMap.has(convertToString(element))) {
                const newList = [
                  {
                    name: name,
                    clientName: clientName,
                    timeZone: timeZone,
                    cron: cron,
                    cronInfo: cronInfo,
                    env: env,
                    exchange: exchange,
                    reportCycle: reportCycle,
                    createdTs: createdTs,
                  },
                ];
                const existingList = [startTimeMap.get(convertToString(element))];
                startTimeMap.set(convertToString(element), existingList.concat(newList));
              } else {
                const list = [
                  {
                    name: name,
                    clientName: clientName,
                    timeZone: timeZone,
                    cron: cron,
                    cronInfo: cronInfo,
                    env: env,
                    exchange: exchange,
                    reportCycle: reportCycle,
                    createdTs: createdTs,
                  },
                ];
                startTimeMap.set(convertToString(element), list);
              }
            });

          });
          console.log("hello1", startTimeMap);

        })
        .catch((err) => {
          console.log(err);
        });
    });
  };

  return (
    <div>
      <div>
        <FullCalendar
          plugins={[timeGridPlugin]}
          timeZone="local"
          initialView="timeGridDay"
          allDaySlot={false}
          eventMinHeight={20}
          customButtons={{
            reload: {
              text: "Reload",
              click: function () {
                handleReloadClick();
              },
            },
          }}
          headerToolbar={{
            left: "reload,prev,next",
            right: "today,timeGridDay,timeGridWeek",
          }}
          eventClick={function (arg) {
            alert(arg.event.title);
            alert(arg.event.start);
          }}
          slotDuration="00:05:00"
          //events={events}
        />
      </div>
    </div>
  );
}

export default Home;
